SELECT lotteryId, lotteryFullNo, lotteryNum, lotteryDate FROM ( SELECT c.lottery_type lotteryId, l.lottery_full_no lotteryFullNo, l.lottery_num lotteryNum, l.lottery_date lotteryDate FROM t_concern c INNER JOIN t_lottery_info l ON c.lottery_type = l.lottery_id and c.is_concern = 0 WHERE 1 = 1
AND c.uid = #{uid,jdbcType=VARCHAR}
ORDER BY l.lottery_date DESC ) t GROUP BY t.lotteryId DESC
LIMIT #{limit,jdbcType=INTEGER}

